mapper.select:
		select * from t_transfer where 1=1
				
		and t_name=#{t_name}   	
    	and p_name=#{p_name}   	
    	and applytime >=#{startTime}   	
    	and applytime <=#{endTime}	
    	and State=#{state}

		select * from c_collection where 1=1
		
		and c_name=#{c_name}   	
    	and c_main_account=#{c_main_account}    	
  		and c_main_amount=#{c_main_amount}    	
    	and c_sub_account=#{c_sub_account}    	
    	and State=#{state}

		select * from ao_accout_opening where 1=1
				
		and ao_name=#{ao_name}   	
    	and ao_accout_number=#{ao_accout_number}   	
    	and applytime >=#{startTime}   	
    	and applytime <=#{endTime}	
    	  
    	and ao_cardtype=#{ao_cardtype}  
    	and State=#{state}
